	/**
	 * Get all available platforms.
	 *
	 * @return array Platform definitions.
	 */
	public static function get_platforms() {
		return self::$platforms;
	}

	/**
	 * Get a specific platform's data.
	 *
	 * @param string $platform_key Platform identifier.
	 * @return array|null Platform data or null if not found.
	 */
	public static function get_platform( $platform_key ) {
		return isset( self::$platforms[ $platform_key ] ) ? self::$platforms[ $platform_key ] : null;
	}

	/**
	 * Get SVG markup for a platform icon.
	 *
	 * @param string $platform_key Platform identifier.
	 * @param string $color_mode   Color mode: 'brand', 'mono', or hex color.
	 * @return string SVG markup.
	 */
	public static function get_svg( $platform_key, $color_mode = 'brand' ) {
		if ( ! isset( self::$svg_paths[ $platform_key ] ) || ! isset( self::$platforms[ $platform_key ] ) ) {
			return '';
		}

		$platform = self::$platforms[ $platform_key ];
		$path     = self::$svg_paths[ $platform_key ];

		// Determine fill color.
		if ( 'brand' === $color_mode ) {
			$fill = $platform['color'];
		} elseif ( 'mono' === $color_mode ) {
			$fill = 'currentColor';
		} else {
			// Custom color (hex value).
			$fill = sanitize_hex_color( $color_mode ) ? $color_mode : 'currentColor';
		}

		$svg = sprintf(
			'<svg role="img" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="%s"><title>%s</title><path d="%s"/></svg>',
			esc_attr( $fill ),
			esc_attr( $platform['name'] ),
			esc_attr( $path )
		);

		return $svg;
	}

	/**
	 * Get platforms that have Transistor API mapping.
	 *
	 * @return array Platforms with transistor_key set.
	 */
	public static function get_transistor_platforms() {
		$transistor_platforms = array();
		foreach ( self::$platforms as $key => $platform ) {
			if ( ! empty( $platform['transistor_key'] ) ) {
				$transistor_platforms[ $key ] = $platform;
			}
		}
		return $transistor_platforms;
	}

	/**
	 * Map Transistor API field to platform key.
	 *
	 * @param string $transistor_key Transistor API field name.
	 * @return string|null Platform key or null if not found.
	 */
	public static function get_platform_by_transistor_key( $transistor_key ) {
		foreach ( self::$platforms as $key => $platform ) {
			if ( isset( $platform['transistor_key'] ) && $platform['transistor_key'] === $transistor_key ) {
				return $key;
			}
		}
		return null;
	}
}
